// Decides which type of output depending on person authorization
// 1 - No face detected (treated as unknown)
// 2 - Face recognized but does not fulfill >90% accuracy (same)
// 3 - Face recognized (OK)

// 1
if (msg.payload.person == "[]") {
    return [null, msg];
} else {
    // 2
    if (msg.payload.person[0] == "Unknown person") {
        let person = msg.payload.person[1];
        if (person == "Unknown person" || person == undefined) {
            return [null, msg];
        } else {
            let matches = msg.payload.matches;
            let samples = msg.payload.samples;
            let frac = (100*(matches/samples)).toFixed(2);

            node.warn(`${msg.payload.person[1]} has ${matches} matches out of ${samples} samples, meaning ${frac}% of confidence.`);

            // return [null, null]; // this output blocks the flow
            return [null, msg];
        }
    } else {
        // 3
        return [msg, null];
    }
}

